import './App.css';
import React, { useContext, useEffect, createContext, useState } from 'react';
import { BrowserRouter as Router, Route, Switch, useHistory } from 'react-router-dom';
import NavBar from './components/NavBarComponent/NavBarComponent';
import Register from './components/AuthComponents/RegisterComponent';
import Login from './components/AuthComponents/LoginComponent';

export const NavBarContext = createContext();

function App() {
    const history = useHistory()
    const [type, navBarState] = useState('login');

    useEffect(() => {
        const user = JSON.parse(localStorage.getItem("user"))
        if (user) {
            // dispatch({ type: "USER", payload: user })
            history.push('register')
        } else {
            history.push('/login')
        }
    }, [])

    return (
        <Router>
            <NavBarContext.Provider value={[type, navBarState]}>
                <NavBar />
            </NavBarContext.Provider>
            <Switch>
                <Route path="/register">
                    <Register />
                </Route>
                <Route path="/login">
                    <Login />
                </Route>
            </Switch>
        </Router>
    );
}

export default App;
