import React, { useState } from 'react';

const Pagination = ({ postsPerPage, totalPosts, paginate }) => {
    const pageNumbers = [];
    const [buttonState, setColor] = useState({ selected: false, id: null });

    const changeState = (number) => {
        setColor({ selected: true, id: number });
    }

    for (let i = 1; i <= Math.ceil(totalPosts / postsPerPage); i++) {
        pageNumbers.push(i);
    }

    return (
        <div className="row">
            <div className="col s4 offset-l4">
                <ul className='pagination'>
                    {pageNumbers.map(number => (
                        <li key={number} className='page-item'>
                            <button style={{ backgroundColor: buttonState.id === number ? 'grey' : 'white' }} onClick={() => { paginate(number); changeState(number); }} className='page-link'>
                                {number}
                            </button>
                        </li>
                    ))}
                </ul>

            </div>

        </div>
    );
};

export default Pagination;
