import React, { useState } from "react";
import M from "materialize-css";

const validateEmail = (email) => {
    return !/^(([^<>()[\]\\.,;:\s@"]+(\.[^<>()[\]\\.,;:\s@"]+)*)|(".+"))@((\[[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}])|(([a-zA-Z\-0-9]+\.)+[a-zA-Z]{2,}))$/.test(
        email
    )
}

const Register = () => {

    const [email, emailState] = useState('');
    const [password, passwordState] = useState('');
    const [name, nameState] = useState('');
    const [confirmPassword, confirmPasswordState] = useState('');

    const handleRegister = () => {
        if (password !== confirmPassword) {
            return M.toast({
                html: "Passwords dont match",
                classes: "#c62828 red darken-3",
            });
        }
        if (validateEmail(email)) {
            return M.toast({
                html: "Invalid Email",
                classes: "#c62828 red darken-3",
            });
        }
    }

    return (
        <div className="container" id="login__container">
            <div className="row" style={{ textAlign: 'center', marginTop: "8rem" }}>
                <h1>Welcome !!</h1>
            </div>
            <form action="">
                <div className="row input__field">
                    <div className="input-field col s12 l6 offset-l3" >
                        <i className="material-icons prefix">email</i>
                        <input requried placeholder="Email" id="icon_prefix" type="email" className="validate" value={email} onChange={(e) => { emailState(e.target.value) }} />

                    </div>
                </div>
                <div className="row input__field">
                    <div className="input-field col s12 l6 offset-l3" >
                        <i className="material-icons prefix">accessibility</i>
                        <input required placeholder="Name" id="icon_prefix" type="email" className="validate" value={name} onChange={(e) => { nameState(e.target.value) }} />

                    </div>
                </div>
                <div className="row">
                    <div className="input-field col col s12 l6 offset-l3" >
                        <i className="material-icons prefix">account_circle</i>
                        <input required placeholder="Password" id="icon_telephone" type="password" className="validate" value={password} onChange={(e) => { passwordState(e.target.value) }} />

                    </div>
                </div>
                <div className="row">
                    <div className="input-field col col s12 l6 offset-l3" >
                        <i className="material-icons prefix">check_circle</i>
                        <input required placeholder="Confrim Password" id="icon_telephone" type="password" className="validate" value={confirmPassword} onChange={(e) => { confirmPasswordState(e.target.value) }} />

                    </div>
                </div>
                <div className="row">
                    <div className="col s6 offset-s3" style={{ textAlign: 'center' }}>
                        <button className="btn waves-effect waves-light #64b5f6 blue lighten-2" onClick={() => handleRegister()}>
                            Register
                        </button>
                    </div>
                </div>
            </form>
        </div>


    );
}

export default Register;
