import React, { useState, useEffect, useContext } from "react";
import { Link } from "react-router-dom";
import { UserContext } from '../../App';
import PostCard from './PostCards';



const Home = () => {
    const { state, dispatch } = useContext(UserContext);
    const [postList, setPostList] = useState([]);
    const [loading, setLoading] = useState(false);
    useEffect(() => getPosts(), []);

    const getPosts = async () => {
        setLoading(true);
        console.log(localStorage.getItem("jwt"));
        const res = await fetch("https://twimbit-api.herokuapp.com/api/post/allPosts", {
            headers: {
                Authorization: "Bearer " + localStorage.getItem("jwt"),
            },
        });

        const data = await res.json();
        setPostList(data.posts);
        setLoading(false);
    }



    if (loading) {
        return (
            <div className="loader" style={{
                position: "absolute",
                bottom: "50%",
                right: " 50%"
            }}>
                <div className="preloader-wrapper small active">
                    <div className="spinner-layer spinner-green-only">
                        <div className="circle-clipper left">
                            <div className="circle"></div>
                        </div>
                        <div className="gap-patch">
                            <div className="circle"></div>
                        </div>
                        <div className="circle-clipper right">
                            <div className="circle"></div>
                        </div>
                    </div>
                </div>
            </div>
        )
    }

    const content = postList.map((post) => {
        return (
            <div className="row">
                <div className="col s4 offset-l4">
                    <PostCard postItem={post} />
                </div>
            </div>
        )
    })
    // const rows = [...Array(Math.ceil(postList.length / 5))];
    // const postRows = rows.map((row, idx) => postList.slice(idx * 4, idx * 4 + 4));
    // const content = postRows.map((row, idx) => (
    //     <div className="row" key={idx}>
    //         {row.map((post) => {
    //             return (
    //                 <div className="col s4 offset-l4" >
    //                     <PostCard postItem={post} />
    //                 </div>
    //             );
    //         })}
    //     </div>)
    // );



    return (
        <div className="container-fluid">

            <div className="row" style={{ textAlign: 'center' }}>
                <h1>These are the posts</h1>
            </div>
            {content}
            <Link to={{ pathname: "/postDetail", state: { postItem: {} } }} style={{ color: 'blue' }}>Know More...</Link>

        </div >

    );
}

export default Home;
