import React, { useContext, useState } from 'react';
import { Link } from "react-router-dom";
import { UserContext } from "../../App";
import M from "materialize-css";


const PostCard = ({ postItem }) => {

    const { state, dispatch } = useContext(UserContext);
    const [likeCount, setCount] = useState(postItem.likes.length);
    const [liked, setLikedState] = useState(false);

    const likePost = async () => {
        setCount(likeCount + 1);
        setLikedState(true);
        const res = await fetch("https://twimbit-api.herokuapp.com/api/post/likePost", {
            method: "post",
            headers: {
                "Content-Type": "application/json",
                Authorization: "Bearer " + localStorage.getItem("jwt"),
            },
            body: JSON.stringify({
                postId: postItem._id,
            }),
        });
        if (res.status === 200) {

        } else {
            M.toast({ html: "Some Error Occured", classes: "#c62828 red darken-3" });
        }

    }

    const unlikePost = async () => {
        setCount(likeCount - 1);
        setLikedState(false);
        const res = await fetch("https://twimbit-api.herokuapp.com/api/post/unlikePost", {
            method: "post",
            headers: {
                "Content-Type": "application/json",
                Authorization: "Bearer " + localStorage.getItem("jwt"),
            },
            body: JSON.stringify({
                postId: postItem._id,
            }),
        });

        if (res.status === 200) {

        } else {
            M.toast({ html: "Some Error Occured", classes: "#c62828 red darken-3" });
        }

    }

    return (
        <div className="card blue-grey darken-1" style={{ width: "36rem" }} key={postItem._id}>
            <div className="card-content white-text">
                <span style={{ color: 'black', fontSize: "2rem" }} className="card-title">{postItem.postedBy.name.toUpperCase()}</span>
                <div>
                    <h2 style={{ color: 'white' }}>{postItem.title}</h2>
                </div>
                <Link to='/' style={{ color: 'blue' }}>Know More...</Link>

                <div style={{ marginTop: "1rem" }} >
                    {liked ?
                        (<i className="material-icons" onClick={() => { likePost() }}>thumb_down</i>)
                        :
                        (<i className="material-icons" onClick={() => { unlikePost() }}>thumb_up</i>)
                    }
                </div>
                <h6>{likeCount} likes</h6>
                <div>
                    <h5 style={{ color: 'white' }}>Comments</h5>
                </div>
                <div>
                    <h6 style={{ color: 'black' }}>{postItem.postedBy.name}</h6>
                    <h4 style={{ color: 'black', padding: '0px' }}> Comment</h4>
                    <hr />
                </div>
                <div>
                    <form onSubmit={(e) => { e.preventDefault(); }}>
                        <input type="text" placeholder="Add a Comment" />
                        <input type="submit" value="Comment" className="btn waves-effect waves-light #64b5f6 blue lighten-2" />
                    </form>
                </div>
            </div>

        </div >
    );
}

export default PostCard;
