import { useHistory, useLocation } from 'react-router-dom';
import { UserContext } from "../../App";
import M from "materialize-css";

const PostDetails = () => {

    const history = useHistory();
    const location = useLocation()
    const postItem = location.state


    return (
        <div className="card blue-grey darken-1" style={{ width: "36rem" }} key={postItem._id}>
            <div className="card-content white-text">
                <span style={{ color: 'black', fontSize: "2rem" }} className="card-title">{postItem.postedBy.name.toUpperCase()}</span>
                <div>
                    <h2 style={{ color: 'white' }}>{postItem.title}</h2>
                </div>
                <div>
                    <p>{postItem.content}</p>
                </div>

            </div>

        </div >
    );
}


